import { Col, Row } from 'react-grid-system';
import { useHistory } from "react-router-dom";
import english from '../Images/icons/english.svg';
import hebrew from '../Images/icons/hebrew.svg';
import YHNlogo from '../Images/YHN.logo.png';

function LanguageSelectorComp() {

    const history = useHistory();

    const slctEN = () =>
    {
        history.push('/en')
    }

    const slctHE = () =>
    {
        history.push('/he')
    }



    return (
      <div className="App">
          <div id="welcome">

              <img style={{ height : "20%", width : 'auto' }} src={YHNlogo} alt="YHN Logo" />

              <br/>

              <Row>
                  <Col xl={6}>
                      <img id="icons" src={english} alt="English" onClick={slctEN} />
                  </Col>

                  <Col xl={6}>
                       <img id="icons" src={hebrew} alt="Hebrew" onClick={slctHE} />  
                  </Col>
              </Row>
              </div>







      </div>
    );
  }

  export default LanguageSelectorComp;
