import { Col, Row } from 'react-grid-system';
import israelflag from '../Images/icons/israel.flag.svg';
import React, { useState } from 'react';
import MainPageComp from './Mainpage';
import MainPageHEComp from './HEcomps/MainpageHE';

function LanguageSelectorComp() {

    const [page, setPage] = useState("./Home");


    return (
      <div className="App">
          <div id="welcome">


              <Row>
                  <Col xl={6}>
                      <button>English</button>
                  </Col>

                  <Col xl={6}>
                      <button>Hebrew</button>
                  </Col>
              </Row>
              </div>


              <div>
                  {page === "./EN" && <MainPageComp />}
                  {page === "./HE" && <MainPageHEComp />}
              </div>


      </div>
    );
  }

  export default LanguageSelectorComp;
