import { Col, Row } from 'react-grid-system';
import israelflag from '../Images/icons/israel.flag.svg';
import MainPageComp from './Mainpage';
import MainPageHEComp from './HEcomps/MainpageHE';
import { Route, Switch } from "react-router-dom";
import { useState } from "react";
import { useHistory } from "react-router-dom";

function LanguageSelectorComp() {

    const history = useHistory();

    const slctEN = () =>
    {
        history.push('/en')
    }

    const slctHE = () =>
    {
        history.push('/he')
    }



    return (
      <div className="App">
          <div id="welcome">


              <Row>
                  <Col xl={6}>
                      <button onClick={slctEN}>English</button>
                  </Col>

                  <Col xl={6}>
                       <button onClick={slctHE}>Hebrew</button>
                  </Col>
              </Row>
              </div>

              <Route path="/en">
                  <MainPageComp />
              </Route>

              <Route path="/he">
                  <MainPageHEComp />
              </Route>





      </div>
    );
  }

  export default LanguageSelectorComp;
