import { useState } from 'react';
import { Col, Row } from 'react-grid-system';
import fullstack from '../Images/fullstack.logo.png'
import meanstack from '../Images/meanstack.logo.png'
import mernstack from '../Images/mernstack.logo.png'

function PortfolioComp() {

    const [page, setPage] = useState("Portfolio");
    return (
      <div className="App">

          <h1 style={{color : 'white'}}>Portfolio</h1>

          <div class="container">
              <Row>
                  <Col lg={6}><img src={fullstack} alt="Full Stack Logo" /></Col>
                  <Col lg={6} style={{color : 'white'}}><p>This is my first Full Stack Project. On this project I need to create 4 main pages which are: "Login", "Departments", "Employees" and "Shifts". We can manage data from the data base directly trough these pages using CRUD operations. This website is managed by users so we need to create a Login page in order for them to connect with a password. Each user has a limited number of actions per day and afterwards they are not allowed to connect for the next 24 hours. Once a user is logged-in, His name and number of actions available are shown on every page of the website. In addition to the CRUD operations on the employee page, we need to create a Search Box. This Search Box should be able to find an employee according his first name, last name or department.</p></Col>

              </Row>
              <Row>
                  <Col lg={12}><img src={meanstack} alt="Mean Stack Logo" /></Col>
              </Row>
              <Row>
                  <Col lg={6}><img src={mernstack} alt="Mern Stack Logo" /></Col>
              </Row>
          </div>





      </div>
    );
  }

  export default PortfolioComp;
